import { motion } from 'framer-motion';
import Link from 'next/link';

const titlesIn = {
  initial: { opacity: 0, x: '-10vh' },
  animate: { opacity: 1, x: 0 },
  exit: { opacity: 0, x: '10vh' },
};

const stagger = {
  animate: {
    transition: {
      staggerChildren: 0.5,
    },
  },
};

export default () => {
  return (
    <motion.div
      initial={{ opacity: 0, x: '-10vh' }}
      animate={{ opacity: 1, x: 0 }}
      exit={{ opacity: 0, x: '10vh' }}
    >
      <motion.div variants={stagger} className="container d-flex">
        <div className="p-5">
          {/* <img src="/images/portrait-2.jpg" className="pres-photo" /> */}
          <div className="stripe"></div>
        </div>
        <div className="p-5">
          <h2>
            Quentin Roux - Juriste /<strong> Codeur</strong>
          </h2>
          <hr />
          <motion.div variants={titlesIn}>
            <h5>
              <strong>Pourquoi</strong>
            </h5>
          </motion.div>

          <h5>
            Juriste de formation, je suis finalement tombé dans la programmation
            en cherchant à coder des smartcontracts sur blockchain ethereum.
          </h5>
          <br />
          <hr />
          <h5>
            <strong>Comment</strong>
          </h5>
          <h5>
            Après une période de découverte en autodidacte, j'ai fini par suivre
            une formation de développeur Fullstack au Wagon Marseille (batch
            #212).
          </h5>
          <br />
          <hr />
          <h5>
            <strong>Maintenant</strong>
          </h5>
          <h5>
            Je développe en langage Ruby et Javascript ( Backend/Frontend ).
          </h5>
          <br />
        </div>
        <Link href="/">
          <a>index</a>
        </Link>
      </motion.div>
    </motion.div>
  );
};
