import { motion } from 'framer-motion';
import Link from 'next/link';

const projects = [
  {
    title: 'projet1',
    url: 'https://picsum.photos/250/180',
  },
  {
    title: 'projet2',
    url: 'https://picsum.photos/250/180',
  },
  {
    title: 'projet3',
    url: 'https://picsum.photos/250/180',
  },
];

const easing = [0.6, -0.05, 0.01, 0.99];

const fadeInUp = {
  initial: {
    y: 60,
    opacity: 0,
    transition: { duration: 0.6, ease: easing },
  },
  animate: {
    y: 0,
    opacity: 1,
    transition: {
      duration: 0.6,
      ease: easing,
    },
  },
};

const stagger = {
  animate: {
    transition: {
      staggerChildren: 0.1,
    },
  },
};

export default () => (
  <motion.div initial="initial" animate="animate" exit={{ opacity: 0 }}>
    <div className="container center">
      <motion.div
        animate={{ opacity: 1 }}
        initial={{ opacity: 0 }}
        className="title"
      >
        <h1>PORTFOLIO</h1>
      </motion.div>
      <motion.div variants={stagger} className="project-container">
        {projects.map((project) => (
          <motion.div
            variants={fadeInUp}
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.95 }}
            className="project"
          >
            <motion.img
              initial={{ opacity: 0 }}
              animate={{ opacity: 1 }}
              transition={{ delay: 0.2 }}
              key={project.title}
              src={project.url}
              width={250}
            />
            <div className="project-info">
              <h4>{project.title}</h4>
              <span>{project.title}</span>
            </div>
          </motion.div>
        ))}
      </motion.div>
    </div>
  </motion.div>
);

