import 'bootstrap/dist/css/bootstrap.css';
import { AnimatePresence } from 'framer-motion';
import Head from 'next/head';

export default ({ Component, pageProps, router }) => {
  return (
    <div
      className="container-fluid bg-success d-flex justify-content-center align-items-center p-5"
      style={{
        height: '100vh',
        backgroundSize: 'cover',
        backgroundImage:
          "linear-gradient(rgba(0, 0, 0, 0.1), rgba(0, 0, 0, 0.1)), url('https://images.unsplash.com/photo-1552154357-aaba12e68e64?ixlib=rb-1.2.1&auto=format&fit=crop&w=1350&q=80')",
      }}
    >
      <Head>
        <link href="/static/styles.css" rel="stylesheet" />
        <link
          href="https://fonts.googleapis.com/css2?family=Montserrat:wght@100;300;500;700&display=swap"
          rel="stylesheet"
        />
      </Head>
      <AnimatePresence exitBeforeEnter>
        <Component {...pageProps} key={router.route} />
      </AnimatePresence>
    </div>
  );
};
